void solve() {
    int n, x;
    cin >> n >> x;
    vector<string> s(n);
    for (int i = 0; i < n; i++) {
      cin >> s[i];
    }
    int ans = 0;
    for (int i = 0; i < n; i++) {
      if (x >= int(s[i].size())) {
        x -= int(s[i].size());
        ans += 1;
      } else {
        break;
      }
    }
    cout << ans << '\n';
}

int32_t main() {
  fast_IO();
  cerr << fixed << setprecision(10);
  auto start = std::chrono::high_resolution_clock::now();
  int t = 1;
  cin >> t;
  // for (int i = 1; i <= t; ++i) {
  //  cout << "Case #" << i << ": ";
  // 	solve();
  // }
  while (t--) {
    solve();
  }
  auto stop = std::chrono::high_resolution_clock::now();
  auto duration =
      std::chrono::duration_cast<std::chrono::nanoseconds>(stop - start);
  // time()
  return 0;
}

