/* Main Colors */
@define-color background        #0c0c12; /* Deep dark background */
@define-color foreground        #ffae42; /* Warm orange for text, matches fiery tones */

/* Workspace Button Colors */
@define-color active-bg         #ff4d4d; /* Bright red for active workspace */
@define-color active-fg         #ffffff; /* Pure white for text contrast */
@define-color hover-bg          #ffa347; /* Warm orange for hover background */
@define-color hover-fg          #ffffff; /* Pure white for hover text */

/* Wlogout Colors */
@define-color bar-bg            #0c0c12;
@define-color main-bg           #0c0c12;
@define-color main-fg           #ffae42; /* Consistent orange for foreground text */
@define-color wb-act-bg         #ff4d4d; /* Red for active buttons */
@define-color wb-act-fg         #ffffff; /* White for text contrast */
@define-color wb-hvr-bg         #ffa347; /* Orange for hover buttons */
@define-color wb-hvr-fg         #ffffff;

/* Main Background */
window#waybar { background: @background; }

/* Drop Shadow */
window#waybar > box {
    background-color: transparent;
    box-shadow: 0px 0px 6px 2px rgba(245, 215, 110, 0.8); /* Golden glow */
    margin: 2px 2px 2px 2px;
}

/* Tooltip Styling */
tooltip {
    color: @foreground;
    background: @background;
    border: solid;
    border-width: 1.5px;
    border-radius: 7px;
    border-color: @foreground;
}

/* Workspace Button */
#workspaces button {
    color: @foreground;
    border-radius: 8px;
    box-shadow: none;
    margin-top: 2px;
    margin-right: 0px;
    margin-bottom: 2px;
    margin-left: 0px;
    padding: 0px;
    padding-right: 2px;
    padding-left: 2px;
    animation: ws_normal 20s ease-in-out 1;
}

/* Active Workspace Button */
#workspaces button.active {
    color: @active-fg;
    background: @active-bg;
    text-shadow: 0px 0px 4px rgba(245, 107, 107, 0.8); /* Fiery glow for active workspace */
    box-shadow: 0px 0px 6px 2px rgba(245, 107, 107, 0.8);
    margin-right: 2px;
    margin-left: 2px;
    padding-right: 6px;
    padding-left: 6px;
    animation: ws_active 20s ease-in-out 1;
    transition: all 0.4s cubic-bezier(.55,-0.68,.48,1.682);
}

/* Hover Effect for Workspace Buttons */
#workspaces button:hover {
    color: @hover-fg;
    background: @hover-bg;
    box-shadow: 0px 0px 6px 2px rgba(217, 139, 57, 0.8); /* Orange glow for hover */
    animation: ws_hover 20s ease-in-out 1;
    transition: all 0.3s cubic-bezier(.55,-0.68,.48,1.682);
}

