function rmfzf() {
    local target
    target=$(
        (find . -maxdepth 1 -type f 2>/dev/null; \
        find . -maxdepth 1 -type d 2>/dev/null) \
        | fzf --preview 'ls -al {}' --tac
    ) && trash "$target" && echo "Moved to trash: $target" || echo "Action cancelled"
}

zle -N rmfzf
bindkey '^X^A' rmfzf

