    "text" : "Suspend",
    "keybind" : "u"
}

{
    "label" : "shutdown",
    "action" : "sh ~/.dwm/scripts/uptime shutdown && systemctl poweroff",
    "text" : "Shutdown",
    "keybind" : "s"
}

{
    "label" : "hibernate",
    "action" : "systemctl hibernate",
    "text" : "Hibernate",
    "keybind" : "h"
}

{
    "label" : "reboot",
    "action" : "sh ~/.dwm/scripts/uptime reboot && systemctl reboot",
    "text" : "Reboot",
    "keybind" : "r"
}
