        reader = PdfReader(uploaded_file)
        writer = PdfWriter()

        for page in reader.pages:
            writer.add_page(page)

        writer.encrypt(password)

        output_stream = BytesIO()
        writer.write(output_stream)
        output_stream.seek(0)
